export function getDiagnosisType(scores: number[]): DiagnosisType {
    const totalScore = scores.reduce((a, b) => a + b, 0);
    const avgScore = totalScore / scores.length;

    if (avgScore <= 1.5) return DIAGNOSIS_TYPES[0]; // å …å®Ÿæ´¾
    if (avgScore <= 2.5) return DIAGNOSIS_TYPES[1]; // åˆç†ä¸»ç¾©è€…
    if (avgScore <= 3.5) return DIAGNOSIS_TYPES[2]; // æ„Ÿè¦šæ´¾
    return DIAGNOSIS_TYPES[3]; // å†’é™ºå®¶
}
